import React from "react";
import { Link } from "react-router-dom";
import "./Home.css";
import { Tabs } from "antd";
import { library } from "./helpers/albumList";
import { useNFTTokenIds } from "hooks/useNFTTokenIds";
import { Card, Alert, Image } from "antd";
import { useMoralisDapp } from "providers/MoralisDappProvider/MoralisDappProvider";
import { useIPFS } from "hooks/useIPFS";

const styles = {
  image: {
    width: "150px",
    marginBottom: "10px"
  },
};

const { TabPane } = Tabs;

const Home = () => {
  const fallbackImg =
    "data:image/png;base64,";
  const contractAddress = "0x3eEDc39f195EE85cD6e2e3226911158c82b3Ab61";
  const { NFTTokenIds, totalNFTs, fetchSuccess } = useNFTTokenIds(contractAddress);
  const { chainId, walletAddress } = useMoralisDapp();
  const { resolveLink } = useIPFS();

  return (
    <>
      <Tabs defaultActiveKey="1" centered>
        <TabPane tab="FEATURED" key="1">
          <h1 className="featuredTitle">Today Is The Day</h1>
          <div className="albums">
            {library.map((e, i) => (
              <div key={i} className="albumSelection">
                <Link to={{
                  pathname: "/album",
                  state: e
                }}>
                  <Image
                    preview={false}
                    src={resolveLink(e.image) || "error"}
                    fallback={fallbackImg}
                    alt="album image cover"
                    style={styles.image}
                  />
                  <p>{e.title}</p>
                </Link>
              </div>
            ))}
          </div>
        </TabPane>
        <TabPane tab="MUSIC NFTs" key="4">
          {totalNFTs !== undefined && (
            <>
              {!fetchSuccess && (
                <>
                  <Alert
                    message="Unable to fetch all NFT metadata... We are searching for a solution, please try again later!"
                    type="warning"
                  />
                  <div style={{ marginBottom: "10px" }}></div>
                </>
              )}
              <div className="albums">
                {NFTTokenIds.slice(0, 20).map((e, i) => {
                  return e.metadata.type == "music" ?
                  (
                    <div key={i} className="albumSelection">
                      <Link to={{
                        pathname: "/album",
                        state: e
                      }} state={e}>
                        <Image
                          preview={false}
                          src={resolveLink(e.image) || "error"}
                          fallback={fallbackImg}
                          alt="video thumbnail image"
                          style={styles.image}
                        />
                        <p>{e.metadata.name}</p>
                      </Link>
                    </div>
                    ) : 
                   null
                }
                )}
              </div>
            </>
          )}
        </TabPane >

        <TabPane tab="VIDEO NFTS" key="5">
          {totalNFTs !== undefined && (
            <>
              {!fetchSuccess && (
                <>
                  <Alert
                    message="Unable to fetch all NFT metadata... We are searching for a solution, please try again later!"
                    type="warning"
                  />
                  <div style={{ marginBottom: "10px" }}></div>
                </>
              )}
              <div className="albums">
                {NFTTokenIds.slice(0, 20).map((e, i) => {
                  return e.metadata.type == "video" ?

                  (
                    <div key={i} className="albumSelection">
                      <Link to={{
                        pathname: "/videoalbum",
                        state: e
                      }} state={e}>
                        <Image
                          preview={false}
                          src={resolveLink(e.image) || "error"}
                          fallback={fallbackImg}
                          alt="video thumbnail image"
                          style={styles.image}
                        />
                        <p>{e.metadata.name}</p>
                      </Link>
                    </div>
                    ) : 
                    null
                }
                )}
              </div>
            </>
          )}
        </TabPane >
      </Tabs>
    </>
  );
};

export default Home;